using Nethereum.ABI.FunctionEncoding.Attributes;
using Nethereum.Contracts;

namespace LockerApi.Services
{

    internal class DeploymentMessage : ContractDeploymentMessage
    {

        public static string BYTECODE = "";

        public DeploymentMessage() : base(BYTECODE) { }

    }

    [Function("addLog")]
    internal class AddLogToDeviceFunctionMessage : FunctionMessage
    {
        [Parameter("uint", "devId", 1)]
        public uint DevId { get; set; }

        [Parameter("string", "log", 2)]
        public string Log { get; set; }
    }

    [Function("getLogs", "string")]
    internal class GetLogsFunctionMessage : FunctionMessage
    {
        [Parameter("uint", "devId", 1)]
        public uint DevId { get; set; }
    }
}